users = User.all

5.times do
  random_f_name = f_names[rand(0..199)]
  random_l_name = l_names[rand(0..199)]
  @user = User.create({
    "first_name" => random_f_name,
    "last_name" => random_l_name,
    "email" => "#{random_f_name}@#{random_l_name}.com",
    "password" => "password"})
  end
puts User.all 

